package key_test

import (
	"encoding/hex"

	. "github.com/iotaledger/iota.go/consts"
	"github.com/iotaledger/iota.go/curl"
	"github.com/iotaledger/iota.go/kerl"
	"github.com/iotaledger/iota.go/signing/key"
	sponge "github.com/iotaledger/iota.go/signing/utils"
	. "github.com/iotaledger/iota.go/trinary"

	. "github.com/onsi/ginkgo"
	. "github.com/onsi/gomega"
)

var nullHashTrits = make(Trits, HashTrinarySize)

var _ = Describe("Key", func() {

	Context("Shake()", func() {
		Context("null entropy", func() {
			var (
				// compare against official eXtended Keccak Code Package https://github.com/XKCP/XKCP
				// KeccakSum --shake256 --hex --outputbits 31104 <(printf '\x00%.0s' {1..48})
				shakeHex = ""
				// the SHAKE256 sum converted to trits
				shakeTrits Trits
			)

			BeforeSuite(func() {
				shakeSum, _ := hex.DecodeString(shakeHex)
				for i := 0; i < len(shakeSum); i += HashBytesSize {
					trits, _ := kerl.KerlBytesToTrits(shakeSum[i : i+HashBytesSize])
					shakeTrits = append(shakeTrits, trits...)
				}
			})

			It("returns the correct key for SecurityLevelLow", func() {
				key, err := key.Shake(nullHashTrits, SecurityLevelLow)
				Expect(err).ToNot(HaveOccurred())
				Expect(key).To(And(HaveLen(SignatureMessageFragmentTrinarySize), Equal(shakeTrits[:len(key)])))
			})

			It("returns the correct key for SecurityLevelMedium", func() {
				key, err := key.Shake(nullHashTrits, SecurityLevelMedium)
				Expect(err).ToNot(HaveOccurred())
				Expect(key).To(And(HaveLen(2*SignatureMessageFragmentTrinarySize), Equal(shakeTrits[:len(key)])))
			})

			It("returns the correct key for SecurityLevelHigh", func() {
				key, err := key.Shake(nullHashTrits, SecurityLevelHigh)
				Expect(err).ToNot(HaveOccurred())
				Expect(key).To(And(HaveLen(3*SignatureMessageFragmentTrinarySize), Equal(shakeTrits[:len(key)])))
			})
		})

		Context("invalid entropy", func() {
			It("returns error for empty entropy", func() {
				_, err := key.Shake(nil, SecurityLevelMedium)
				Expect(err).To(HaveOccurred())
			})

			It("returns error for invalid trits ", func() {
				trits := make(Trits, HashTrinarySize)
				trits[0] = MaxTritValue + 1

				_, err := key.Shake(trits, SecurityLevelMedium)
				Expect(err).To(HaveOccurred())
			})

			It("returns error when last trit is non-zero", func() {
				trits := make(Trits, HashTrinarySize)
				trits[HashTrinarySize-1] = 1

				_, err := key.Shake(trits, SecurityLevelMedium)
				Expect(err).To(HaveOccurred())
			})
		})
	})

	Context("Sponge()", func() {
		var s sponge.SpongeFunction

		Context("Kerl", func() {
			kerlTrytes := ""

			BeforeEach(func() {
				s = kerl.NewKerl()
			})

			It("returns the correct key for SecurityLevelLow", func() {
				key, err := key.Sponge(nullHashTrits, SecurityLevelLow, s)
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(key)).To(And(HaveLen(SignatureMessageFragmentSizeInTrytes), Equal(kerlTrytes[:len(key)/TritsPerTryte])))
			})

			It("returns the correct key for SecurityLevelMedium", func() {
				key, err := key.Sponge(nullHashTrits, SecurityLevelMedium, s)
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(key)).To(And(HaveLen(2*SignatureMessageFragmentSizeInTrytes), Equal(kerlTrytes[:len(key)/TritsPerTryte])))
			})

			It("returns the correct key for SecurityLevelHigh", func() {
				key, err := key.Sponge(nullHashTrits, SecurityLevelHigh, s)
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(key)).To(And(HaveLen(3*SignatureMessageFragmentSizeInTrytes), Equal(kerlTrytes[:len(key)/TritsPerTryte])))
			})
		})

		Context("Curl-P-81", func() {
			curlTrytes := ""

			BeforeEach(func() {
				s = curl.NewCurlP81()
			})

			It("returns the correct key for SecurityLevelLow", func() {
				key, err := key.Sponge(nullHashTrits, SecurityLevelLow, s)
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(key)).To(And(HaveLen(SignatureMessageFragmentSizeInTrytes), Equal(curlTrytes[:len(key)/TritsPerTryte])))
			})

			It("returns the correct key for SecurityLevelMedium", func() {
				key, err := key.Sponge(nullHashTrits, SecurityLevelMedium, s)
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(key)).To(And(HaveLen(2*SignatureMessageFragmentSizeInTrytes), Equal(curlTrytes[:len(key)/TritsPerTryte])))
			})

			It("returns the correct key for SecurityLevelHigh", func() {
				key, err := key.Sponge(nullHashTrits, SecurityLevelHigh, s)
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(key)).To(And(HaveLen(3*SignatureMessageFragmentSizeInTrytes), Equal(curlTrytes[:len(key)/TritsPerTryte])))
			})
		})

		Context("invalid entropy", func() {
			It("returns error for empty entropy", func() {
				_, err := key.Sponge(nil, SecurityLevelMedium, nil)
				Expect(err).To(HaveOccurred())
			})

			It("returns error for invalid trits ", func() {
				trits := make(Trits, HashTrinarySize)
				trits[0] = MaxTritValue + 1

				_, err := key.Sponge(trits, SecurityLevelMedium, nil)
				Expect(err).To(HaveOccurred())
			})

			It("returns error when Kerl is used and the last trit is non-zero", func() {
				trits := make(Trits, HashTrinarySize)
				trits[HashTrinarySize-1] = 1

				_, err := key.Sponge(trits, SecurityLevelMedium, kerl.NewKerl())
				Expect(err).To(HaveOccurred())
			})
		})

		It("SpongeFunction is reset", func() {
			h := kerl.NewKerl()
			_, err := key.Sponge(nullHashTrits, SecurityLevelMedium, h)
			Expect(err).ToNot(HaveOccurred())
			// absorb is not possible after a squeeze
			Expect(h.Absorb(nullHashTrits)).ToNot(HaveOccurred())
			// compare against new Kerl instance
			newKerl := kerl.NewKerl()
			_ = newKerl.Absorb(nullHashTrits)
			Expect(h.MustSqueeze(HashTrinarySize)).To(Equal(newKerl.MustSqueeze(HashTrinarySize)))
		})
	})
})
