package signing_test

import (
	. "github.com/iotaledger/iota.go/consts"
	. "github.com/iotaledger/iota.go/signing"
	. "github.com/iotaledger/iota.go/trinary"
	. "github.com/onsi/ginkgo"
	. "github.com/onsi/gomega"
)

var _ = Describe("Signing", func() {

	const seed = "ZLNM9UHJWKTTDEZOTH9CXDEIFUJQCIACDPJIXPOWBDW9LTBHC9AQRIXTIHYLIIURLZCXNSTGNIVC9ISVB"

	subseeds := []Trytes{
		"CEFLDDLMF9TO9ZLLTYXIPVFIJKAOFRIQLGNYIDZCTDYSWMNXPYNGFAKHQDY9ABGGQZHEFTXKWKWZXEIUD",
		"PIOVKH9GTWJYTYLUXIXOFSKCNABQFN9MPXJERMXZVWCDGFTABEVZIIXDM9YQ9LSGKFGVLZKTYJRSONDGX",
		"9BIPKFYQPUGBAHXVIW9XSW9QWJNDYPDEAMKEDSQPYQBDPTHENATOVZCFZCGICWUCISPGHBSXLSDQIBMBX",
		"TTYHP9NDJPTC9EWDKTZLTC9URASRHGMVIMXHQRKSRRUU9HIRBVWWSUNDCMKNLLCCSXRRMVMIZAPFREEUW",
		"ABWFWWOFSBVYXDKXJOJOJNQOEHXRDRNROJJHN9HYVOBQGCJ9JWEBSXICAELNCTJPSFBNZXVRETFCKDYXW",
	}

	keys := []Trytes{
		"",
		"",
		"",
		"",
		"",
	}

	digests := []Trytes{
		"MUVADERKIZGMEYJVHGVWBKMQMMXOPWYVOXYPNAGDNKBLHWIBUALWLWSSNDXLYAIIWX9NQRRAOQIVIHWLAIRTWWSF9TGEIKFGMCDWNIXPIYKRTSBHJIONSTSSVUCBYHS9SOZB9PSAOSJUIYQYTUV9NXLZCZWHUALYWW",
		"EZGWDQBCNPEV9ULATDZDGXTVMKZVMRUP9XNEDPVUKVJGAYIEOKWZEHJQDRRIYKTZYIAWBYQORCTFDSNQXUNKNKNDZEOOLI9EPZLJVFJIOBUMIZLFMIFPKMRZMRTZMOZNUIKZA9CZWLHZAUDKRBNDOCTPRDUFWJSZNB",
		"SNRBRLCMIBXJUXLFEKXXZOCWZXRDDWZNDWBONJRGBQZWVAUTWXHEBDCBKMQ9IWLIOCGUGOWQBYDP9BJRAAJPHH9QACSGYWZWIHHIOIPADBEJSNMLV9HFXPCERSGUX99AAOYOPY9PELMNGJVSPVUFVQVOIDMRPYAIWY",
		"BCCLFMWJXSK9BIXDPFHIQWUZVXYDT9EXBXUCF9XQWPNYLKPY9KSQVMJIJLYRQQLROMFBWVWTJYHCSLWUDCOOKSLRPJHXGSAAFRBZCWIDH9W9DAURROS9SLXGOPNEFQRNTZWTU9GQTARDHTVJKGWYJBCVGJXBJHXT9W",
		"XIVOAVWOX9FQCG9HTE9BSOTKTTCFUIDKSGJIEZHHGEWPZIHOROJOFWHAATLVHSYHNPREHJTMNRRBNYBU9PFVBAITTIEDRZFUMAVWCRRAKFVE9AWIINHKTNZXIQJQDVO9HSNBJFPUIOIEGU9ZLTANJIBA99HNPT9WSZ",
	}

	addresses := []Trytes{
		"CLAAFXEY9AHHCSZCXNKDRZEJHIAFVKYORWNOZAGFPAZYNTSLCXUAG9WBSXBRXYEDPVPLXYVDCBCEKRUBD",
		"CDWOADSZWJMDCLYKEDMPIBTYIFAUUAGM9ZQYDKARBUKFXW9LDRQLNG9MI9DGXSOSPDDFFWWJCB9PTGXPW",
		"VVFGHNRFUQEQILXZYUIHWQFUVEEBQCXCUUENADOKRLTVGULYBNMITSYHVRWMAPKPERRLLTC9ELIWSMMMD",
		"IEKMSNDJVIQMLEFUMVQUUFOMI9RWVMJUXKYABPVOYWVMOOVVABYJUKMHZSXDSACYNTEKBCXRJGRJCKXRY",
		"QELVIIRYZZFJSRKMJSDAEOQJRSAWCGMZOGMTBDNJPIOXQTUGMVPCYLWHGHREKDRRVABPULZI9BOWZQPF9",
	}

	fragments := []Trytes{
		"",
		"",
	}

	multisig := struct {
		address    Hash
		bundleHash Hash
		fragments  []Trytes
	}{
		address:    "FZLSQZYQUZUJILMZNZTNGDHFGFGDNEDFZIPPAGDAAITZCGXVPQYVIQGBYRTLHNKCFDMVHGVACIEGVJKDD",
		bundleHash: "XNGWZRGYLYSGEKPDZHPZJYWKTYOAIDDYDDGYKZPCRWIBDRINW9LJVW9FPGDUQN9JDSSBAGRZOGLAFFTFD",
		fragments: []Trytes{
			"",
			"",
			"",
			"",
			"",
			"",
			"",
			"",
		},
	}

	noncanonical := struct {
		address    Hash
		bundleHash Hash
		fragments  []Trytes
	}{
		address:    "KSNWNNLGLHIBVFMULCEXDQLXMGGVKLACPPDNKSQYLBPPHISXGCNDZHEGCTQII9PRMRB9TXTBZ9BZTOSGW",
		bundleHash: "MDOXFKXNNBIFHILRZLZUQISYN9ZLVZCQHCQQCHHTJUHPRBWMEDRYEHLFZQRLLIWESFDDPUDPHBLZLUMCC",
		fragments: []Trytes{
			"",
			"",
		},
	}

	Context("Subseed()", func() {
		It("returns the correct subseed", func() {
			for i := 0; i < len(subseeds); i++ {
				subseed, err := Subseed(seed, uint64(i))
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(subseed)).To(Equal(subseeds[i]))
			}
		})

		It("returns an error for invalid seed", func() {
			_, err := Subseed("asdf", 0)
			Expect(err).To(HaveOccurred())
		})
	})

	Context("Digests()", func() {
		It("returns the correct digests", func() {
			for i := 0; i < len(digests); i++ {
				digs, err := Digests(MustTrytesToTrits(keys[i]))
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(digs)).To(Equal(digests[i]))
			}
		})
	})

	Context("Address()", func() {
		It("returns the correct address", func() {
			for i := 0; i < len(addresses); i++ {
				addr, err := Address(MustTrytesToTrits(digests[i]))
				Expect(err).ToNot(HaveOccurred())
				Expect(MustTritsToTrytes(addr)).To(Equal(addresses[i]))
			}
		})
	})

	Context("NormalizedBundleHash()", func() {
		It("should return the normalized bundle hash fragment", func() {
			expected := []int8{8, 1, 10, -12, 8, 1, -13, 6, 5, -12, -7, -9, -8, 9, -11, 3, 12, 7, 0, 13, 9, -11, 5, -13, 4, 6, -11, -3, -10, -10, -6, 7, -8, 2, 12, 2, 8, 13, 11, -1, 0, -3, -5, 3, -6, -8, -4, 9, 11, 10, -12, -12, 8, -8, 13, 13, 13, 13, 13, -5, 12, -11, -7, 1, 11, 13, -11, -6, -9, -2, 1, -2, 4, 0, -12, -13, -12, 4, -5, -12, -4}
			normalized := NormalizedBundleHash("VAJOHANFEOTRSIPCLG9MIPENDFPLQQUGSBLBHMKZ9XVCUSWIKJOOHSPWJAXVLPTAKMPURYAYD9ONODVOW")
			Expect(normalized).To(Equal(expected))
			expected = []int8{-13, -13, -13, -7, -8, 10, -5, -12, 6, 10, 7, 4, 3, -1, 11, -5, -9, 0, 1, 10, 2, -2, -5, 11, 3, 3, 12, -8, 6, 11, -12, 7, 12, -6, -3, -12, 6, -2, -10, -2, 8, 9, -5, -3, 7, -6, -11, 9, 4, 8, -8, -3, 7, -3, -10, 0, 9, 6, 10, -9, 12, -13, -9, 2, -2, 11, 7, -2, 6, 9, 2, -2, -6, -1, 0, -4, -5, 8, -9, -7, -3}
			normalized = NormalizedBundleHash("LMHGSJVOFJGDCZKVR9AJBYVKCCLGFKOGLUXOFYQYHIVXGUPIDHSXGXR9IFJRLNRBYKGYFIBYUZ9WVHRTX")
			Expect(normalized).To(Equal(expected))
		})
	})

	Context("SignatureFragment()", func() {
		It("should return the correct signature fragments", func() {
			normalized := NormalizedBundleHash("VAJOHANFEOTRSIPCLG9MIPENDFPLQQUGSBLBHMKZ9XVCUSWIKJOOHSPWJAXVLPTAKMPURYAYD9ONODVOW")
			frags := make([]Trytes, 2)
			keyTrits := MustTrytesToTrits(keys[0])
			for i := 0; i < int(SecurityLevelMedium); i++ {
				frag, err := SignatureFragment(normalized[i*HashTrytesSize/3:(i+1)*HashTrytesSize/3],
					keyTrits[i*KeyFragmentLength:(i+1)*KeyFragmentLength])
				Expect(err).ToNot(HaveOccurred())
				frags[i] = MustTritsToTrytes(frag)
			}
			Expect(frags).To(Equal(fragments))
		})
	})

	Context("SignatureAddress()", func() {
		It("should return correct address", func() {
			sigAddr, err := SignatureAddress(fragments, "VAJOHANFEOTRSIPCLG9MIPENDFPLQQUGSBLBHMKZ9XVCUSWIKJOOHSPWJAXVLPTAKMPURYAYD9ONODVOW")
			Expect(err).ToNot(HaveOccurred())
			Expect(sigAddr).To(Equal(addresses[0]))
		})

		It("should return wrong address for invalid signature", func() {
			sigAddr, err := SignatureAddress(fragments, "BBBBHANFEOTRSIPCLG9MIPENDFPLQQUGSBLBHMKZ9XVCUSWIKJOOHSPWJAXVLPTAKMPURYAYD9ONODVOW")
			Expect(err).ToNot(HaveOccurred())
			Expect(sigAddr).ToNot(Equal(addresses[0]))
		})

		Context("non-canonical fragments", func() {
			It("should return correct address", func() {
				sigAddr, err := SignatureAddress(noncanonical.fragments, noncanonical.bundleHash)
				Expect(err).ToNot(HaveOccurred())
				Expect(sigAddr).To(Equal(noncanonical.address))
			})
		})

		Context("multisig", func() {
			It("should return correct address", func() {
				sigAddr, err := SignatureAddress(multisig.fragments, multisig.bundleHash)
				Expect(err).ToNot(HaveOccurred())
				Expect(sigAddr).To(Equal(multisig.address))
			})

			It("should return wrong address for invalid signature", func() {
				sigAddr, err := SignatureAddress(multisig.fragments, NullHashTrytes)
				Expect(err).ToNot(HaveOccurred())
				Expect(sigAddr).ToNot(Equal(multisig.address))
			})
		})

		Context("invalid arguments", func() {
			It("empty fragments", func() {
				_, err := SignatureAddress([]Trytes{}, multisig.bundleHash)
				Expect(err).To(HaveOccurred())
			})

			It("invalid fragment length", func() {
				fragments := []Trytes{
					MustPad("", SignatureMessageFragmentSizeInTrytes+1),
				}
				_, err := SignatureAddress(fragments, multisig.bundleHash)
				Expect(err).To(HaveOccurred())
			})

			It("invalid hash", func() {
				_, err := SignatureAddress(multisig.fragments, "22")
				Expect(err).To(HaveOccurred())
			})
		})
	})

	Context("ValidateSignatures()", func() {
		It("should return true for valid signature", func() {
			valid, err := ValidateSignatures(addresses[0], fragments, "VAJOHANFEOTRSIPCLG9MIPENDFPLQQUGSBLBHMKZ9XVCUSWIKJOOHSPWJAXVLPTAKMPURYAYD9ONODVOW")
			Expect(err).ToNot(HaveOccurred())
			Expect(valid).To(BeTrue())
		})

		It("should return false for invalid signature", func() {
			valid, err := ValidateSignatures(addresses[0], fragments, "BBBBHANFEOTRSIPCLG9MIPENDFPLQQUGSBLBHMKZ9XVCUSWIKJOOHSPWJAXVLPTAKMPURYAYD9ONODVOW")
			Expect(err).ToNot(HaveOccurred())
			Expect(valid).To(BeFalse())
		})

		Context("non-canonical fragments", func() {
			It("should return true for valid signature", func() {
				valid, err := ValidateSignatures(noncanonical.address, noncanonical.fragments, noncanonical.bundleHash)
				Expect(err).ToNot(HaveOccurred())
				Expect(valid).To(BeTrue())
			})
		})

		Context("multisig", func() {
			It("should return true for valid signature", func() {
				valid, err := ValidateSignatures(multisig.address, multisig.fragments, multisig.bundleHash)
				Expect(err).ToNot(HaveOccurred())
				Expect(valid).To(BeTrue())
			})

			It("should return false for invalid signature", func() {
				valid, err := ValidateSignatures(multisig.address, multisig.fragments, NullHashTrytes)
				Expect(err).ToNot(HaveOccurred())
				Expect(valid).To(BeFalse())
			})
		})
	})

	Context("Digest()", func() {
		It("should return correct digest", func() {
			normalized := NormalizedBundleHash("VAJOHANFEOTRSIPCLG9MIPENDFPLQQUGSBLBHMKZ9XVCUSWIKJOOHSPWJAXVLPTAKMPURYAYD9ONODVOW")
			normalizedFragments := make([][]int8, 3)
			for i := 0; i < 3; i++ {
				normalizedFragments[i] = normalized[i*27 : (i+1)*27]
			}

			dds := []Trytes{
				"MUVADERKIZGMEYJVHGVWBKMQMMXOPWYVOXYPNAGDNKBLHWIBUALWLWSSNDXLYAIIWX9NQRRAOQIVIHWLA",
				"IRTWWSF9TGEIKFGMCDWNIXPIYKRTSBHJIONSTSSVUCBYHS9SOZB9PSAOSJUIYQYTUV9NXLZCZWHUALYWW",
			}

			for i := 0; i < len(fragments); i++ {
				digest, err := Digest(normalizedFragments[i%3], MustTrytesToTrits(fragments[i]))
				Expect(err).ToNot(HaveOccurred())
				Expect(TritsToTrytes(digest)).To(Equal(dds[i]))
			}
		})
	})

})
